	<item>
		<title>10 Tips to Maximize Your Small Kitchen</title>
		<link><?php echo $row['url']; ?>/maximize-small-kitchen/</link>
		
		<dc:creator><![CDATA[briannabeetstra]]></dc:creator>
		<pubDate>Mon, 20 Sep 2021 16:03:02 +0000</pubDate>
