		<title>2021 Condo Design Trends for <?php echo $row['country']; ?></title>
		<link><?php echo $row['url']; ?>/condo-design-trends-<?php echo $row['country']; ?>/</link>
		
		<dc:creator><![CDATA[briannabeetstra]]></dc:creator>
		<pubDate>Mon, 21 Dec 2020 19:27:18 +0000</pubDate>
				<category><![CDATA[<?php echo $row['country']; ?> Condo]]></category>
